import React from 'react';
import 'antd/dist/antd.css';

import classes from './Slider.module.css';

import { Carousel, Radio } from 'antd';
import { Container } from 'react-bootstrap';


const Slider = () => {
  const [dotPosition, setDotPosition] = React.useState('top');

  const handlePositionChange = ({ target: { value } }) => {
    setDotPosition(value);
  };

  return (

    <Container>
      <Radio.Group onChange={handlePositionChange} value={dotPosition}>

      </Radio.Group>
      <Carousel dotPosition={'left'} className={classes.sliderStyle}>
        <div>
          <h3 className={classes.sliderStyle}>1</h3>
        </div>
        <div>
          <h3 className={classes.sliderStyle}>2</h3>
        </div>
        <div>
          <h3 className={classes.sliderStyle}>3</h3>
        </div>
        <div>
          <h3 className={classes.sliderStyle}>4</h3>
        </div>
      </Carousel>
      </Container>

  );
};

export default Slider;
